# -*- coding: utf8-*-
import itertools
alphabet = 'abcdefghijklmnopqrstuvwxyz'

def letterify(msg):
    print(msg)
    msg=msg.replace('-','m')
    msg=msg.replace('.','a')
    msg=msg.replace('/','p')
    msg=msg.replace(' ','s')

    return msg






def encode(msg, key):
    secret = ''
    key_length = len(key)
    alphabet_length = len(alphabet)

    for i, char in enumerate(msg):
        msgInt = alphabet.find(char)
        encInt = alphabet.find(key[i % key_length])

        if msgInt == -1 or encInt == -1:
            return ''

        encoded = (msgInt + encInt) % alphabet_length
        secret += alphabet[encoded]

    return secret


def vigenere_decode(enc, key):
    """Function that decodes a string with Vigenere cipher. The decoded
       string is returned.
    """
    dc = ''
    key_length = len(key)
    alphabet_length = len(alphabet)

    for i, char in enumerate(enc):
        msgInt = alphabet.find(char)
        encInt = -alphabet.find(key[i % key_length])

        if msgInt == -1 or encInt == -1:
            dc+=' '

        decoded = (msgInt + encInt) % alphabet_length
        dc += alphabet[decoded]

    return dc

